import React from 'react';
import '../App.css';
import { Button } from './Button';
import './HeroSection.css';
import vid1 from './videos/video-2.mp4';

function HeroSection() {
  const openLink = (url) => {
    window.open(url, '_blank', 'noopener,noreferrer');
  };
  return (
    <div className='hero-container'>
      <video src={vid1} autoPlay loop muted />
      <h1>Christy's Portfolio</h1>
      <p>Coding with Data</p>
      <div className='hero-btns'>
          <Button
            className='btns'
            buttonStyle='btn--outline'
            buttonSize='btn--large'
            onClick={() => openLink('https://github.com/ChristyYuen')}
          >
            GitHub
          </Button>
          <Button
          className='btns'
          buttonStyle='btn--outline'
          buttonSize='btn--large'
          onClick={() => openLink('https://www.linkedin.com/in/yuenchristy/')}
          >
           LinkedIn
          </Button>
          <Button
            className='btns'
            buttonStyle='btn--outline'
            buttonSize='btn--large'
            onClick={() => openLink('/path/to/resume.pdf')}
          >
            Resume
          </Button>
      </div>
    </div>
  );
}

export default HeroSection;
