import React from 'react';
import './AboutMe.css';
import AboutMeItem from './AboutMeItem';
import iceland from './images/iceland.jpg';
import grad from './images/Grad.JPG';

function AboutMe() {
  return (
    <div className='aboutme'>
      <h1>About Me</h1>

      {/* Add a brief introduction or other text */}
      <p className='aboutme__intro'>
        Hi! I'm Christy, a Computer Science graduate with a passion for technology and travel. Here's a bit more about me!
      </p>

      <div className='aboutme__container'>
        <div className='aboutme__wrapper'>
            <ul className='aboutme__items'>
                <AboutMeItem
                    src={iceland}
                    label='Location: Iceland'
                    title="Travel Lover!"
                    subtitle="Been to Iceland & Australia."
                />
                <AboutMeItem
                    src={grad} 
                    label='UCSC Graduate' 
                    title="Majored in Computer Science." 
                    subtitle="Minor in Technology and Management." 
                />
            </ul>
        </div>

        {/* Add more descriptive text after the cards */}
        <div className='aboutme__additional-info'>
          <p>
            I’m always looking for opportunities to learn new things, whether it’s through travel or coding projects.
            I’ve enjoyed combining my love for exploration with my technical skills to create unique experiences.
          </p>
          <p>
            Feel free to explore more about me below, or check out my portfolio for my latest projects!
          </p>
        </div>
      </div>
    </div>
  );
}

export default AboutMe;
